mapper.select:
select
id, `name`, scene_id, table_id, `type`, title, create_by, create_time, update_time,
style_priority,x_axis, y_axis, custom_attr, custom_style, custom_filter, snapshot,
get_auths(id,'chart',#{userId}) as `privileges`
from (select GET_V_AUTH_MODEL_ID_P_USE (#{userId}, 'chart') cids) t,chart_view

    FIND_IN_SET(chart_view.id,cids)

        and scene_id = #{sceneId,jdbcType=VARCHAR}


        and id = #{id,jdbcType=VARCHAR}



    order by ${sort}

select id, `name`, scene_id, table_id, `type`, title, create_by, create_time, update_time, style_priority,x_axis, y_axis, custom_attr, custom_style, custom_filter, snapshot, authInfo.privileges as `privileges` from (select GET_V_AUTH_MODEL_ID_P_USE (#{userId}, 'chart') cids) t,chart_view left join ( SELECT auth_source, group_concat( DISTINCT sys_auth_detail.privilege_extend ) as `privileges` FROM ( `sys_auth` LEFT JOIN `sys_auth_detail` ON (( `sys_auth`.`id` = `sys_auth_detail`.`auth_id` ))) WHERE sys_auth_detail.privilege_value = 1 AND sys_auth.auth_source_type = 'chart' AND ( ( sys_auth.auth_target_type = 'dept' AND sys_auth.auth_target IN ( SELECT dept_id FROM sys_user WHERE user_id = #{userId} ) ) OR ( sys_auth.auth_target_type = 'user' AND sys_auth.auth_target = #{userId} ) OR ( sys_auth.auth_target_type = 'role' AND sys_auth.auth_target IN ( SELECT role_id FROM sys_users_roles WHERE user_id = #{userId} ) ) ) GROUP BY `sys_auth`.`auth_source` ) authInfo on chart_view.id = authInfo.auth_source
FIND_IN_SET(chart_view.id,cids)
and scene_id = #{sceneId,jdbcType=VARCHAR}
and name like CONCAT('%', #{name},'%')
order by ${sort}
